import React, { useState, useRef, useEffect } from 'react';
import { Play, Pause, Upload, Volume2, SkipBack, SkipForward } from 'lucide-react';
import './App.css';

function App() {
  const [audioFile, setAudioFile] = useState(null);
  const [images, setImages] = useState([]);
  const [currentSlide, setCurrentSlide] = useState(0);
  const [isPlaying, setIsPlaying] = useState(false);
  const [duration, setDuration] = useState(0);
  const [currentTime, setCurrentTime] = useState(0);
  const [volume, setVolume] = useState(0.7);
  const [audioError, setAudioError] = useState('');

  const audioRef = useRef(null);
  const fileInputRef = useRef(null);
  const imageInputRef = useRef(null);

  // Auto slide change every 10 seconds when playing
  useEffect(() => {
    if (isPlaying && images.length > 1) {
      const interval = setInterval(() => {
        setCurrentSlide(prev => (prev + 1) % images.length);
      }, 10000); // 10 seconds

      return () => clearInterval(interval);
    }
  }, [isPlaying, images.length]);

  // Handle audio file upload
  const handleAudioUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      console.log('File selected:', file.name, file.type, file.size);

      // In local environment, we can use createObjectURL safely
      try {
        const url = URL.createObjectURL(file);
        console.log('Object URL created:', url);
        setAudioFile(url);
        setAudioError('');
        setCurrentTime(0);
        setIsPlaying(false);
      } catch (error) {
        console.error('Error creating object URL:', error);
        setAudioError('Error loading file: ' + error.message);
      }
    }
    event.target.value = '';
  };

  // Handle image files upload
  const handleImagesUpload = (event) => {
    const files = Array.from(event.target.files);
    console.log('Images selected:', files.length);

    if (files.length === 0) return;

    const imageUrls = [];
    files.forEach(file => {
      if (file.type.startsWith('image/')) {
        try {
          const url = URL.createObjectURL(file);
          imageUrls.push({ url, name: file.name });
          console.log('Image URL created:', file.name);
        } catch (error) {
          console.error('Error creating image URL:', error);
        }
      }
    });

    setImages(imageUrls);
    setCurrentSlide(0);
    event.target.value = '';
  };

  // Audio event handlers
  const handleLoadedMetadata = () => {
    if (audioRef.current) {
      setDuration(audioRef.current.duration);
      console.log('Audio metadata loaded, duration:', audioRef.current.duration);
      setAudioError('');
    }
  };

  const handleTimeUpdate = () => {
    if (audioRef.current) {
      setCurrentTime(audioRef.current.currentTime);
    }
  };

  const handleAudioError = (e) => {
    console.error('Audio error:', e);
    const audio = e.target;
    if (audio && audio.error) {
      const errorMessages = {
        1: 'Loading aborted',
        2: 'Network error', 
        3: 'Decode error - file corrupted?',
        4: 'Format not supported'
      };
      const message = errorMessages[audio.error.code] || 'Unknown error';
      setAudioError(`Audio error: ${message}`);
      console.error('Audio error code:', audio.error.code, message);
    }
    setIsPlaying(false);
  };

  const handlePlayPause = async () => {
    if (!audioRef.current) return;

    try {
      if (isPlaying) {
        audioRef.current.pause();
        setIsPlaying(false);
        console.log('Audio paused');
      } else {
        console.log('Attempting to play audio...');
        console.log('Audio readyState:', audioRef.current.readyState);
        console.log('Audio src:', audioRef.current.src);

        await audioRef.current.play();
        setIsPlaying(true);
        setAudioError('');
        console.log('Audio playing successfully');
      }
    } catch (error) {
      console.error('Play error:', error);
      setAudioError(`Play error: ${error.message}`);
      setIsPlaying(false);
    }
  };

  const handleVolumeChange = (e) => {
    const newVolume = parseFloat(e.target.value);
    setVolume(newVolume);
    if (audioRef.current) {
      audioRef.current.volume = newVolume;
    }
  };

  const handleSeek = (e) => {
    if (audioRef.current && duration > 0) {
      const seekTime = (e.target.value / 100) * duration;
      audioRef.current.currentTime = seekTime;
      setCurrentTime(seekTime);
    }
  };

  const nextSlide = () => {
    if (images.length > 0) {
      setCurrentSlide((prev) => (prev + 1) % images.length);
    }
  };

  const prevSlide = () => {
    if (images.length > 0) {
      setCurrentSlide((prev) => (prev - 1 + images.length) % images.length);
    }
  };

  const formatTime = (time) => {
    if (isNaN(time)) return '0:00';
    const minutes = Math.floor(time / 60);
    const seconds = Math.floor(time % 60);
    return `${minutes}:${seconds.toString().padStart(2, '0')}`;
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-900 via-blue-900 to-indigo-900 p-6">
      <div className="max-w-4xl mx-auto">
        <h1 className="text-4xl font-bold text-white text-center mb-8">
          Audio Slideshow - Local Version
        </h1>

        {/* Upload Section */}
        <div className="bg-white/10 backdrop-blur-lg rounded-xl p-6 mb-6">
          <div className="grid md:grid-cols-2 gap-4">
            <div>
              <label className="block text-white text-sm font-medium mb-2">
                Upload Audio File
              </label>
              <input
                ref={fileInputRef}
                type="file"
                accept="audio/*"
                onChange={handleAudioUpload}
                className="hidden"
              />
              <button
                onClick={() => fileInputRef.current?.click()}
                className="w-full bg-blue-600 hover:bg-blue-700 text-white font-medium py-3 px-4 rounded-lg flex items-center justify-center gap-2 transition-colors"
              >
                <Upload size={20} />
                Choose Audio File
              </button>
            </div>

            <div>
              <label className="block text-white text-sm font-medium mb-2">
                Upload Images
              </label>
              <input
                ref={imageInputRef}
                type="file"
                accept="image/*"
                multiple
                onChange={handleImagesUpload}
                className="hidden"
              />
              <button
                onClick={() => imageInputRef.current?.click()}
                className="w-full bg-green-600 hover:bg-green-700 text-white font-medium py-3 px-4 rounded-lg flex items-center justify-center gap-2 transition-colors"
              >
                <Upload size={20} />
                Choose Images ({images.length} selected)
              </button>
            </div>
          </div>

          {/* Error Display */}
          {audioError && (
            <div className="mt-4 bg-red-500/20 border border-red-500/50 rounded-lg p-3">
              <p className="text-red-200 text-sm">{audioError}</p>
            </div>
          )}
        </div>

        {/* Slideshow Display */}
        <div className="bg-black/20 backdrop-blur-lg rounded-xl p-6 mb-6">
          <div className="relative aspect-video bg-black rounded-lg overflow-hidden mb-4">
            {images.length > 0 ? (
              <div className="relative w-full h-full">
                <img
                  src={images[currentSlide]?.url}
                  alt={`Slide ${currentSlide + 1}`}
                  className="w-full h-full object-cover transition-opacity duration-500"
                  onError={(e) => console.error('Image load error:', e)}
                />
                <div className="absolute bottom-4 left-4 bg-black/70 text-white px-3 py-1 rounded-full text-sm">
                  {currentSlide + 1} / {images.length} - {images[currentSlide]?.name}
                </div>
                <div className="absolute top-4 right-4 bg-black/70 text-white px-3 py-1 rounded-full text-sm">
                  Auto: 10s per slide
                </div>
              </div>
            ) : (
              <div className="flex items-center justify-center h-full text-white/60">
                <div className="text-center">
                  <Upload size={48} className="mx-auto mb-2 opacity-50" />
                  <p>Upload images to see slideshow</p>
                </div>
              </div>
            )}
          </div>

          {/* Manual slide controls */}
          <div className="flex justify-center gap-2">
            <button
              onClick={prevSlide}
              disabled={images.length <= 1}
              className="bg-white/20 hover:bg-white/30 disabled:opacity-50 disabled:cursor-not-allowed text-white p-2 rounded-lg transition-colors"
            >
              <SkipBack size={20} />
            </button>
            <button
              onClick={nextSlide}
              disabled={images.length <= 1}
              className="bg-white/20 hover:bg-white/30 disabled:opacity-50 disabled:cursor-not-allowed text-white p-2 rounded-lg transition-colors"
            >
              <SkipForward size={20} />
            </button>
          </div>
        </div>

        {/* Audio Controls */}
        <div className="bg-white/10 backdrop-blur-lg rounded-xl p-6">
          {/* Hidden Audio Element */}
          {audioFile && (
            <audio
              ref={audioRef}
              src={audioFile}
              onLoadedMetadata={handleLoadedMetadata}
              onTimeUpdate={handleTimeUpdate}
              onEnded={() => setIsPlaying(false)}
              onError={handleAudioError}
              onCanPlay={() => console.log('Audio can play')}
              preload="metadata"
            />
          )}

          <div className="flex items-center gap-4 mb-4">
            <button
              onClick={handlePlayPause}
              disabled={!audioFile}
              className="bg-blue-600 hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed text-white p-3 rounded-full transition-colors"
            >
              {isPlaying ? <Pause size={24} /> : <Play size={24} />}
            </button>

            <div className="flex-1">
              <div className="flex justify-between text-white text-sm mb-1">
                <span>{formatTime(currentTime)}</span>
                <span>{formatTime(duration)}</span>
              </div>
              <input
                type="range"
                min="0"
                max="100"
                value={duration ? (currentTime / duration) * 100 : 0}
                onChange={handleSeek}
                disabled={!audioFile}
                className="w-full h-2 bg-white/20 rounded-lg appearance-none cursor-pointer slider"
              />
            </div>

            <div className="flex items-center gap-2">
              <Volume2 size={20} className="text-white" />
              <input
                type="range"
                min="0"
                max="1"
                step="0.1"
                value={volume}
                onChange={handleVolumeChange}
                className="w-20 h-2 bg-white/20 rounded-lg appearance-none cursor-pointer slider"
              />
            </div>
          </div>

          {/* Status Display */}
          <div className="bg-white/5 rounded-lg p-3">
            <div className="text-white/80 text-sm space-y-1">
              <div>Audio: {audioFile ? '‚úÖ Loaded' : '‚ùå Not loaded'}</div>
              <div>Status: {isPlaying ? '‚ñ∂Ô∏è Playing' : '‚è∏Ô∏è Paused'}</div>
              <div>Images: {images.length} uploaded</div>
              <div>Ready State: {audioRef.current?.readyState || 0}/4</div>
              <div>Environment: üè† Local (No CSP restrictions)</div>
            </div>
          </div>
        </div>

        {/* Instructions */}
        <div className="bg-white/5 backdrop-blur-lg rounded-xl p-4 mt-6">
          <h3 className="text-white font-bold mb-2">Local Environment Benefits:</h3>
          <ul className="text-white/80 text-sm space-y-1 list-disc list-inside">
            <li>No Content Security Policy restrictions</li>
            <li>Full blob URL support for audio/images</li>
            <li>Better performance and reliability</li>
            <li>Access to localStorage if needed</li>
            <li>Full browser API access</li>
          </ul>
        </div>
      </div>
    </div>
  );
}

export default App;
