import React, { useState, useRef, useEffect } from 'react';
import { Play, Pause, Upload, Volume2, Plus, Download, Edit3, Trash2, Clock, FileText } from 'lucide-react';

const AdvancedSlideshow = () => {
  const [audioFile, setAudioFile] = useState(null);
  const [images, setImages] = useState([]);
  const [currentSlide, setCurrentSlide] = useState(0);
  const [isPlaying, setIsPlaying] = useState(false);
  const [duration, setDuration] = useState(0);
  const [currentTime, setCurrentTime] = useState(0);
  const [volume, setVolume] = useState(0.7);

  // Advanced timing system
  const [slides, setSlides] = useState([]);
  const [isEditingTiming, setIsEditingTiming] = useState(false);
  const [srtFile, setSrtFile] = useState(null);
  const [timingMode, setTimingMode] = useState('manual'); // 'manual' or 'srt'

  const audioRef = useRef(null);
  const fileInputRef = useRef(null);
  const imageInputRef = useRef(null);
  const srtInputRef = useRef(null);

  // Update slide berdasarkan timing
  useEffect(() => {
    if (slides.length > 0 && currentTime > 0) {
      let activeSlide = 0;
      for (let i = slides.length - 1; i >= 0; i--) {
        if (currentTime >= slides[i].startTime) {
          activeSlide = i;
          break;
        }
      }

      if (activeSlide !== currentSlide) {
        setCurrentSlide(activeSlide);
      }
    }
  }, [currentTime, slides, currentSlide]);

  // Convert time string to seconds
  const timeToSeconds = (timeStr) => {
    // Support format: mm:ss atau mm:ss.sss
    const parts = timeStr.split(':');
    if (parts.length === 2) {
      const minutes = parseInt(parts[0]) || 0;
      const seconds = parseFloat(parts[1]) || 0;
      return minutes * 60 + seconds;
    }
    return 0;
  };

  // Convert seconds to time string
  const secondsToTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = (seconds % 60).toFixed(1);
    return `${mins.toString().padStart(2, '0')}:${secs.padStart(4, '0')}`;
  };

  // Parse SRT file
  const parseSRT = (srtText) => {
    const blocks = srtText.trim().split('\n\n');
    const parsedSlides = [];

    blocks.forEach((block, index) => {
      const lines = block.split('\n');
      if (lines.length >= 3) {
        const timeLine = lines[1];
        const textLines = lines.slice(2);

        // Parse time format: 00:00:10,500 --> 00:00:13,240
        const timeMatch = timeLine.match(/(\d{2}:\d{2}:\d{2}),(\d{3})\s*-->\s*(\d{2}:\d{2}:\d{2}),(\d{3})/);

        if (timeMatch) {
          const [, startH, startM, startS, startMs, endH, endM, endS, endMs] = timeMatch;

          const startTime = parseInt(startH) * 3600 + parseInt(startM) * 60 + parseInt(startS) + parseInt(startMs) / 1000;
          const endTime = parseInt(endH) * 3600 + parseInt(endM) * 60 + parseInt(endS) + parseInt(endMs) / 1000;

          // Use first line as text, or combine all text lines
          const text = textLines.join(' ').trim();

          parsedSlides.push({
            id: Date.now() + index,
            text: text,
            startTime: startTime,
            endTime: endTime,
            imageIndex: Math.min(index, images.length - 1)
          });
        }
      }
    });

    return parsedSlides;
  };

  // Handle SRT file upload
  const handleSRTUpload = (event) => {
    const file = event.target.files[0];
    if (file && file.name.endsWith('.srt')) {
      const reader = new FileReader();
      reader.onload = (e) => {
        const srtText = e.target.result;
        console.log('SRT content:', srtText);

        const parsedSlides = parseSRT(srtText);
        console.log('Parsed slides:', parsedSlides);

        if (parsedSlides.length > 0) {
          setSlides(parsedSlides);
          setSrtFile(file.name);
          setTimingMode('srt');
          setCurrentSlide(0);
        } else {
          alert('Could not parse SRT file. Please check the format.');
        }
      };
      reader.readAsText(file);
    } else {
      alert('Please select a valid .srt file');
    }
    event.target.value = '';
  };

  // Handle audio file upload
  const handleAudioUpload = (event) => {
    const file = event.target.files[0];
    if (file && file.type.startsWith('audio/')) {
      const url = URL.createObjectURL(file);
      setAudioFile(url);
      setCurrentTime(0);
      setCurrentSlide(0);
      setIsPlaying(false);
    }
    event.target.value = '';
  };

  // Handle image files upload
  const handleImagesUpload = (event) => {
    const files = Array.from(event.target.files);
    if (files.length === 0) return;

    const imageUrls = [];
    files.forEach(file => {
      if (file.type.startsWith('image/')) {
        const url = URL.createObjectURL(file);
        imageUrls.push({ url, name: file.name });
      }
    });

    setImages(imageUrls);
    setCurrentSlide(0);

    // If no slides yet, create default slides
    if (slides.length === 0 && timingMode === 'manual') {
      createDefaultSlides(imageUrls.length);
    }

    event.target.value = '';
  };

  // Create default manual slides
  const createDefaultSlides = (imageCount) => {
    const defaultSlides = [];
    for (let i = 0; i < imageCount; i++) {
      defaultSlides.push({
        id: Date.now() + i,
        text: `Slide ${i + 1}`,
        startTime: i * 10, // Default 10 seconds per slide
        endTime: (i + 1) * 10,
        imageIndex: i
      });
    }
    setSlides(defaultSlides);
  };

  // Audio event handlers
  const handleLoadedMetadata = () => {
    if (audioRef.current) {
      setDuration(audioRef.current.duration);
    }
  };

  const handleTimeUpdate = () => {
    if (audioRef.current) {
      setCurrentTime(audioRef.current.currentTime);
    }
  };

  const handlePlayPause = async () => {
    if (audioRef.current) {
      try {
        if (isPlaying) {
          audioRef.current.pause();
          setIsPlaying(false);
        } else {
          await audioRef.current.play();
          setIsPlaying(true);
        }
      } catch (error) {
        console.error('Play error:', error);
        setIsPlaying(false);
      }
    }
  };

  const handleVolumeChange = (e) => {
    const newVolume = parseFloat(e.target.value);
    setVolume(newVolume);
    if (audioRef.current) {
      audioRef.current.volume = newVolume;
    }
  };

  const handleSeek = (e) => {
    if (audioRef.current && duration > 0) {
      const seekTime = (e.target.value / 100) * duration;
      audioRef.current.currentTime = seekTime;
      setCurrentTime(seekTime);
    }
  };

  // Manual slide timing editor
  const updateSlideTime = (slideId, field, value) => {
    setSlides(slides.map(slide => 
      slide.id === slideId 
        ? { ...slide, [field]: field === 'text' ? value : timeToSeconds(value) }
        : slide
    ));
  };

  // Add slide at current time
  const addSlideAtCurrentTime = () => {
    const newSlide = {
      id: Date.now(),
      text: `Slide ${slides.length + 1}`,
      startTime: currentTime,
      endTime: currentTime + 10,
      imageIndex: Math.min(slides.length, images.length - 1)
    };

    const updatedSlides = [...slides, newSlide].sort((a, b) => a.startTime - b.startTime);
    setSlides(updatedSlides);
    setTimingMode('manual');
  };

  // Delete slide
  const deleteSlide = (slideId) => {
    setSlides(slides.filter(slide => slide.id !== slideId));
  };

  // Export current timing as SRT
  const exportToSRT = () => {
    let srtContent = '';
    slides.forEach((slide, index) => {
      const startTime = formatSRTTime(slide.startTime);
      const endTime = formatSRTTime(slide.endTime);

      srtContent += `${index + 1}\n`;
      srtContent += `${startTime} --> ${endTime}\n`;
      srtContent += `${slide.text}\n\n`;
    });

    const blob = new Blob([srtContent], { type: 'text/plain' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'slideshow-timing.srt';
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  // Format time for SRT export
  const formatSRTTime = (seconds) => {
    const hours = Math.floor(seconds / 3600);
    const minutes = Math.floor((seconds % 3600) / 60);
    const secs = Math.floor(seconds % 60);
    const ms = Math.floor((seconds % 1) * 1000);

    return `${hours.toString().padStart(2, '0')}:${minutes.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')},${ms.toString().padStart(3, '0')}`;
  };

  const formatTime = (time) => {
    if (isNaN(time)) return '0:00';
    const minutes = Math.floor(time / 60);
    const seconds = Math.floor(time % 60);
    return `${minutes}:${seconds.toString().padStart(2, '0')}`;
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-900 via-blue-900 to-indigo-900 p-6">
      <div className="max-w-7xl mx-auto">
        <h1 className="text-4xl font-bold text-white text-center mb-8">
          Advanced Slideshow with Custom Timing
        </h1>

        {/* Upload Section */}
        <div className="bg-white/10 backdrop-blur-lg rounded-xl p-6 mb-6">
          <div className="grid md:grid-cols-3 gap-4">
            <div>
              <label className="block text-white text-sm font-medium mb-2">
                Upload Audio File
              </label>
              <input
                ref={fileInputRef}
                type="file"
                accept="audio/*"
                onChange={handleAudioUpload}
                className="hidden"
              />
              <button
                onClick={() => fileInputRef.current?.click()}
                className="w-full bg-blue-600 hover:bg-blue-700 text-white font-medium py-3 px-4 rounded-lg flex items-center justify-center gap-2 transition-colors"
              >
                <Upload size={20} />
                Choose Audio
              </button>
            </div>

            <div>
              <label className="block text-white text-sm font-medium mb-2">
                Upload Images
              </label>
              <input
                ref={imageInputRef}
                type="file"
                accept="image/*"
                multiple
                onChange={handleImagesUpload}
                className="hidden"
              />
              <button
                onClick={() => imageInputRef.current?.click()}
                className="w-full bg-green-600 hover:bg-green-700 text-white font-medium py-3 px-4 rounded-lg flex items-center justify-center gap-2 transition-colors"
              >
                <Upload size={20} />
                Images ({images.length})
              </button>
            </div>

            <div>
              <label className="block text-white text-sm font-medium mb-2">
                Upload SRT File (Optional)
              </label>
              <input
                ref={srtInputRef}
                type="file"
                accept=".srt"
                onChange={handleSRTUpload}
                className="hidden"
              />
              <button
                onClick={() => srtInputRef.current?.click()}
                className="w-full bg-yellow-600 hover:bg-yellow-700 text-white font-medium py-3 px-4 rounded-lg flex items-center justify-center gap-2 transition-colors"
              >
                <FileText size={20} />
                SRT Timing
              </button>
            </div>
          </div>

          {/* Timing Mode Indicator */}
          <div className="mt-4 flex items-center justify-center gap-4">
            <div className={`px-3 py-1 rounded-full text-sm ${
              timingMode === 'manual' ? 'bg-blue-600 text-white' : 'bg-white/20 text-white/70'
            }`}>
              Manual Timing
            </div>
            <div className={`px-3 py-1 rounded-full text-sm ${
              timingMode === 'srt' ? 'bg-yellow-600 text-white' : 'bg-white/20 text-white/70'
            }`}>
              SRT Timing {srtFile && `(${srtFile})`}
            </div>
          </div>
        </div>

        <div className="grid lg:grid-cols-2 gap-6">
          {/* Slideshow Display */}
          <div className="bg-black/20 backdrop-blur-lg rounded-xl p-6">
            <div className="relative aspect-video bg-black rounded-lg overflow-hidden mb-4">
              {images.length > 0 && slides.length > 0 ? (
                <div className="relative w-full h-full">
                  <img
                    src={images[slides[currentSlide]?.imageIndex || 0]?.url}
                    alt={`Slide ${currentSlide + 1}`}
                    className="w-full h-full object-cover"
                  />
                  <div className="absolute bottom-0 left-0 right-0 bg-gradient-to-t from-black/80 to-transparent p-6">
                    <p className="text-white text-xl font-bold text-center">
                      {slides[currentSlide]?.text || ''}
                    </p>
                  </div>
                  <div className="absolute top-4 left-4 bg-black/70 text-white px-3 py-1 rounded-full text-sm">
                    {currentSlide + 1} / {slides.length}
                  </div>
                  <div className="absolute top-4 right-4 bg-black/70 text-white px-3 py-1 rounded-full text-sm">
                    {formatTime(currentTime)} / {formatTime(duration)}
                  </div>
                </div>
              ) : (
                <div className="flex items-center justify-center h-full text-white/60">
                  <div className="text-center">
                    <Upload size={48} className="mx-auto mb-2 opacity-50" />
                    <p>Upload audio, images, and set timing</p>
                  </div>
                </div>
              )}
            </div>

            {/* Audio Controls */}
            {audioFile && (
              <div className="bg-white/10 rounded-lg p-4">
                <audio
                  ref={audioRef}
                  src={audioFile}
                  onLoadedMetadata={handleLoadedMetadata}
                  onTimeUpdate={handleTimeUpdate}
                  onEnded={() => setIsPlaying(false)}
                  className="hidden"
                />

                <div className="flex items-center gap-4 mb-4">
                  <button
                    onClick={handlePlayPause}
                    className="bg-blue-600 hover:bg-blue-700 text-white p-3 rounded-full transition-colors"
                  >
                    {isPlaying ? <Pause size={24} /> : <Play size={24} />}
                  </button>

                  <div className="flex-1">
                    <input
                      type="range"
                      min="0"
                      max="100"
                      value={duration ? (currentTime / duration) * 100 : 0}
                      onChange={handleSeek}
                      className="w-full h-2 bg-white/20 rounded-lg appearance-none cursor-pointer"
                    />
                  </div>

                  <div className="flex items-center gap-2">
                    <Volume2 size={20} className="text-white" />
                    <input
                      type="range"
                      min="0"
                      max="1"
                      step="0.1"
                      value={volume}
                      onChange={handleVolumeChange}
                      className="w-20 h-2 bg-white/20 rounded-lg appearance-none cursor-pointer"
                    />
                  </div>
                </div>

                <button
                  onClick={addSlideAtCurrentTime}
                  disabled={!audioFile}
                  className="w-full bg-yellow-600 hover:bg-yellow-700 disabled:opacity-50 text-white font-medium py-2 px-4 rounded-lg flex items-center justify-center gap-2 transition-colors"
                >
                  <Plus size={16} />
                  Add Slide at {formatTime(currentTime)}
                </button>
              </div>
            )}
          </div>

          {/* Timing Editor */}
          <div className="bg-white/10 backdrop-blur-lg rounded-xl p-6">
            <div className="flex items-center justify-between mb-4">
              <h3 className="text-xl font-bold text-white">Timing Editor</h3>
              <div className="flex gap-2">
                <button
                  onClick={() => setSlides([])}
                  disabled={slides.length === 0}
                  className="bg-red-600 hover:bg-red-700 disabled:opacity-50 text-white px-3 py-1 rounded text-sm transition-colors"
                >
                  Clear All
                </button>
                <button
                  onClick={exportToSRT}
                  disabled={slides.length === 0}
                  className="bg-green-600 hover:bg-green-700 disabled:opacity-50 text-white px-3 py-1 rounded text-sm flex items-center gap-1 transition-colors"
                >
                  <Download size={14} />
                  Export SRT
                </button>
              </div>
            </div>

            {/* Slides Timeline */}
            <div className="space-y-3 max-h-96 overflow-y-auto">
              {slides.map((slide, index) => (
                <div
                  key={slide.id}
                  className={`bg-white/5 rounded-lg p-3 border-l-4 transition-colors ${
                    index === currentSlide ? 'border-blue-500 bg-blue-500/20' : 'border-transparent'
                  }`}
                >
                  <div className="grid grid-cols-12 gap-2 items-center text-sm">
                    <div className="col-span-4">
                      <input
                        type="text"
                        value={slide.text}
                        onChange={(e) => updateSlideTime(slide.id, 'text', e.target.value)}
                        className="w-full bg-white/10 text-white text-xs px-2 py-1 rounded border-none outline-none"
                        placeholder="Slide text"
                      />
                    </div>
                    <div className="col-span-3">
                      <input
                        type="text"
                        value={secondsToTime(slide.startTime)}
                        onChange={(e) => updateSlideTime(slide.id, 'startTime', e.target.value)}
                        className="w-full bg-white/10 text-white text-xs px-2 py-1 rounded border-none outline-none"
                        placeholder="Start time"
                      />
                    </div>
                    <div className="col-span-3">
                      <input
                        type="text"
                        value={secondsToTime(slide.endTime)}
                        onChange={(e) => updateSlideTime(slide.id, 'endTime', e.target.value)}
                        className="w-full bg-white/10 text-white text-xs px-2 py-1 rounded border-none outline-none"
                        placeholder="End time"
                      />
                    </div>
                    <div className="col-span-2 flex justify-end">
                      <button
                        onClick={() => deleteSlide(slide.id)}
                        className="text-red-400 hover:text-red-300 p-1 transition-colors"
                      >
                        <Trash2 size={14} />
                      </button>
                    </div>
                  </div>
                </div>
              ))}

              {slides.length === 0 && (
                <div className="text-white/50 text-center py-8">
                  <Clock size={32} className="mx-auto mb-2 opacity-50" />
                  <p>No slides added yet</p>
                  <p className="text-sm">Upload images and add timing, or use SRT file</p>
                </div>
              )}
            </div>
          </div>
        </div>

        {/* Instructions */}
        <div className="bg-white/5 backdrop-blur-lg rounded-xl p-6 mt-6">
          <div className="grid md:grid-cols-2 gap-6">
            <div>
              <h3 className="text-white font-bold mb-3">Manual Timing:</h3>
              <ol className="text-white/80 text-sm space-y-1 list-decimal list-inside">
                <li>Upload audio and images</li>
                <li>Play audio and click "Add Slide at [time]" for each slide</li>
                <li>Edit slide text and timing in the editor</li>
                <li>Export to SRT if needed</li>
              </ol>
            </div>
            <div>
              <h3 className="text-white font-bold mb-3">SRT File Method:</h3>
              <ol className="text-white/80 text-sm space-y-1 list-decimal list-inside">
                <li>Create or download SRT subtitle file</li>
                <li>Upload audio and images</li>
                <li>Upload SRT file - timing will be applied automatically</li>
                <li>Fine-tune timing if needed</li>
              </ol>
            </div>
          </div>

          <div className="mt-4 p-3 bg-blue-500/20 rounded-lg">
            <h4 className="text-blue-200 font-medium mb-2">SRT Format Example:</h4>
            <pre className="text-blue-100 text-xs">
{`1
00:00:00,500 --> 00:00:04,000
First slide text

2
00:00:04,000 --> 00:00:08,500
Second slide text`}
            </pre>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AdvancedSlideshow;
